mapper.select:
select id, job_name, job_desc, job_type, job_next_time, job_rate, job_cron, trigger_status, trigger_last_time, remark, create_time, update_time, create_by, update_by, next_time from job_info where 1=1
and id = #{id}
and job_name like concat('%',#{jobName},'%')
and job_desc like concat('%',#{jobDesc},'%')
and trigger_status = #{triggerStatus}
